import React, { Component, useState } from 'react';
import { StyleSheet, Text, View, TextInput, ScrollView, SafeAreaView } from 'react-native';
import { CommonStyles, CommonColors } from './style';
import { BackButton, Button, Menu } from '../components/Button';
import { UnderlineIcon } from '../components/icons';
import { w, h } from '../utils/scale'

export default ({ navigation }) => {
  const [name, setName] = useState("")
  const [address, setAddress] = useState("")
  const [city, setCity] = useState("")
  const [zipCode, setZipCode] = useState("")
  const [state, setState] = useState("")
  const [email, setEmail] = useState("")

  return (
    <View style={CommonStyles.overflowContainer}>
      <BackButton />
      <SafeAreaView style={CommonStyles.container}>
        <ScrollView style={CommonStyles.container}>
          <View style={[CommonStyles.padding, CommonStyles.paddingT100]}>
            <Text style={CommonStyles.title}>My Rabbit Purse</Text>
            <View style={styles.padding20}>
              <UnderlineIcon />
            </View>
            <View style={styles.section}>
              <Text style={CommonStyles.sectionTitle}>Shipping Details</Text>
            </View>
            <View style={styles.section}>
              <TextInput
                style={CommonStyles.textInput}
                onChangeText={setName}
                value={name}
                placeholder="Full Name *"
                keyboardType="default"
                placeholderTextColor={CommonColors.Dark}
              />
            </View>
            <View style={styles.section}>
              <TextInput
                style={CommonStyles.textInput}
                onChangeText={setAddress}
                value={address}
                placeholder="Street Address *"
                keyboardType="default"
                placeholderTextColor={CommonColors.Dark}
              />
            </View>
            <View style={styles.section}>
              <TextInput
                style={CommonStyles.textInput}
                onChangeText={setCity}
                value={city}
                placeholder="City *"
                keyboardType="default"
                placeholderTextColor={CommonColors.Dark}
              />
            </View>
            <View style={styles.section}>
              <View style={styles.row}>
                <TextInput
                  style={[CommonStyles.textInput, { width: '45%' }]}
                  onChangeText={setState}
                  value={state}
                  placeholder="State *"
                  keyboardType="default"
                  placeholderTextColor={CommonColors.Dark}
                />
                <TextInput
                  style={[CommonStyles.textInput, { width: '45%' }]}
                  onChangeText={setZipCode}
                  value={zipCode}
                  placeholder="ZIP Code *"
                  keyboardType="numeric"
                  placeholderTextColor={CommonColors.Dark}
                />
              </View>
            </View>
            <View style={styles.section}>
              <TextInput
                style={CommonStyles.textInput}
                onChangeText={setEmail}
                value={email}
                placeholder="Email for Confirmation *"
                keyboardType="email-address"
                placeholderTextColor={CommonColors.Dark}
              />
            </View>
          </View>
        </ScrollView>
      </SafeAreaView>
      <Button title="ENTER" onPress={() => navigation.navigate('Thank You')} backgroundColor={CommonColors.Green} style={{ width: '100%' }} />
    </View>
  );
}

const styles = StyleSheet.create({
  padding20: {
    paddingVertical: w(20)
  },
  section: {
    paddingVertical: w(40),
  },
  bottom: {
    backgroundColor: CommonColors.White,
    paddingLeft: w(40),
    paddingVertical: w(20),
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between'
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between'
  }
});
