import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, SafeAreaView, ScrollView, TextInput } from 'react-native';
import LottieView from 'lottie-react-native';
import { CommonStyles, CommonColors } from './style';
import { BackButton, Button, ButtonBack, ButtonReverse, Menu } from '../components/Button';
import { UnderlineIcon } from '../components/icons';
import { w, h } from '../utils/scale'

export default ({ navigation, route }) => {
  let animation = React.createRef();

  useEffect(() => {
    animation.current.play()
  }, [])

  const { card } = route.params

  const [feet, setFeet] = useState('')
  const [inches, setInch] = useState('')

  return (
    <View style={CommonStyles.overflowContainer}>
      <BackButton />
      <SafeAreaView style={CommonStyles.container}>
        <ScrollView style={CommonStyles.container}>
          <View style={[CommonStyles.padding, CommonStyles.paddingT100]}>
            <Text style={CommonStyles.title}>Tie Breaker</Text>
            <View style={styles.padding20}>
              <UnderlineIcon />
            </View>

            <View style={styles.section}>
              <Text style={CommonStyles.subTitle}>Enter Closet To The Pin for any of your selections</Text>
            </View>
            <View style={styles.section}>
              <TextInput
                style={CommonStyles.textInput}
                onChangeText={setFeet}
                value={feet}
                placeholder="Feet"
                keyboardType="default"
                placeholderTextColor={CommonColors.Dark}
              />
            </View>
            <View style={styles.section}>
              <TextInput
                style={CommonStyles.textInput}
                onChangeText={setInch}
                value={inches}
                placeholder="Inches"
                keyboardType="default"
                placeholderTextColor={CommonColors.Dark}
              />
            </View>
            <View style={styles.section}>
              <LottieView
                ref={animation}
                loop={true}
                style={{
                  width: w(400),
                  height: h(400)
                }}
                source={require('../assets/lottie/breaker.json')}
              />
            </View>
          </View>
        </ScrollView>
      </SafeAreaView>
      <Button title="ENTER" onPress={() => navigation.navigate('MyRabbitCards')} backgroundColor={CommonColors.Green} style={{ width: '100%' }} />
    </View>
  );
}

const styles = StyleSheet.create({
  padding20: {
    paddingVertical: w(20)
  },
  section: {
    paddingVertical: w(40),
    alignItems: 'center'
  },
  bottom: {
    backgroundColor: CommonColors.White,
    paddingLeft: w(40),
    paddingVertical: w(20),
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between'
  },
  section: {
    paddingVertical: w(40),
  },
});
