import React, { Component } from 'react';
import { Dimensions, StyleSheet } from 'react-native';

const { width, height } = Dimensions.get('window');

function w(input) {
    return input / 640 * width;
}

function h(input) {
    return input / 1136 * height;
}

export const CommonColors = {
    Green: '#76BC21',
    Black: '#000000',
    White: '#FFFFFF',
    Dark: '#212322',
    Red: '#EE4036',
    Warning: '#F88D2A',
    Grey: '#474848',
    LightGrey: '#B3B3B3'
}

export const CommonStyles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: CommonColors.White,
    },
    overflowContainer: {
        flex: 1,
        overflow: 'hidden'
    },
    centerContainer: {
        alignItems: 'center',
        justifyContent: 'center',
    },
    logoContainer: {
        paddingTop: w(300),
        paddingBottom: w(200),
        width: w(400),
        height: w(400),
        resizeMode: 'contain',
        justifyContent: 'center',
        alignItems: 'center'
    },
    logo: {
        width: w(400),
        height: w(200),
        resizeMode: 'contain'
    },
    paddingVertical: {
        paddingVertical: w(20),
    },
    padding: {
        padding: w(40),
        paddingTop: w(80)
    },
    title: {
        fontFamily: "SourceSansRomanRegular",
        fontSize: w(58),
        lineHeight: w(68),
        color: CommonColors.Dark,
    },
    sectionTitle: {
        fontFamily: "SourceSansRomanRegular",
        fontSize: w(30),
        lineHeight: w(36),
        color: CommonColors.Dark,
    },
    subTitle: {
        fontFamily: "SourceSansRomanLight",
        fontSize: w(48),
        lineHeight: w(56),
        color: CommonColors.Dark,
    },
    rowTitle: {
        fontFamily: "SourceSansRomanLight",
        fontSize: w(24),
        lineHeight: w(28),
        color: CommonColors.Dark,
    },
    smallText: {
        fontFamily: "SourceSansRomanLight",
        fontSize: w(24),
        lineHeight: w(28),
        color: CommonColors.Dark,
    },
    buttonText: {
        fontFamily: "SourceSansRomanLight",
        fontSize: w(16),
        lineHeight: w(28),
        color: CommonColors.Dark,
    },
    playerButtonText: {
        fontFamily: "SourceSansRomanBold",
        fontSize: w(20),
        lineHeight: w(26),
        color: CommonColors.Dark,
    },
    paddingT100: {
        paddingTop: w(100)
    },
    textInput: {
        borderBottomColor: CommonColors.Dark,
        borderBottomWidth: 1,
        fontFamily: "SourceSansRomanLight",
        fontSize: w(32),
        lineHeight: w(36),
        paddingBottom: w(10),
    },
    errorText: {
        fontFamily: "SourceSansRomanBold",
        fontSize: w(28),
        lineHeight: w(36),
        paddingBottom: w(10),
        color: CommonColors.Red
    }
})
