import React, { Component, useEffect, useState } from 'react';
import { StyleSheet, Text, View, SafeAreaView, ScrollView } from 'react-native';
import { useIsFocused } from '@react-navigation/native';
import { connect, useDispatch, useSelector } from 'react-redux';
import { getAllGolfers } from '../store/redux/golfers/actions';
import { UnderlineIcon } from '../components/icons';
import { Menu } from '../components/Button';
import { CommonColors, CommonStyles } from './style';

const OverallRankings = ({ navigation }) => {
  const isFocused = useIsFocused()
  const state = useSelector(state => state.golfers)
  const auth = useSelector(state => state.auth)
  const dispatch = useDispatch()
  const [golfers, setGolfers] = useState([])

  useEffect(() => {
    setGolfers(state.golfers)
  }, [state.golfers])

  useEffect(() => {
    isFocused && dispatch(getAllGolfers())
  }, [isFocused])

  return (
    <View style={CommonStyles.overflowContainer}>
      <Menu />
      <SafeAreaView style={CommonStyles.container}>
        <ScrollView style={CommonStyles.container}>
          <View style={[CommonStyles.padding, CommonStyles.paddingT100]}>
            <Text style={CommonStyles.title}>My Overall Ranking</Text>
            <View style={styles.padding20}>
              <UnderlineIcon />
            </View>
            <View style={styles.row}>
              <Text style={CommonStyles.sectionTitle}>USER NAME</Text>
              <Text style={CommonStyles.sectionTitle}>RANKING</Text>
            </View>

            {golfers && golfers.length !== 0 &&
              golfers.map((golfer, index) => <View style={styles.row} key={index}>
                <Text style={[CommonStyles.rowTitle, { color: CommonColors.Dark }]} key={golfer.id}>{golfer.first_name} {golfer.last_name}</Text>
                <Text style={[CommonStyles.sectionTitle, , { color: CommonColors.sectionTitle }]}>{golfer.rank}</Text>
              </View>)}

          </View>
        </ScrollView>
      </SafeAreaView>
    </View>
  );
}

const mapStateToProps = (state) => ({
  loading: state.golfers.loading,
  error: state.golfers.error,
});

export default connect(mapStateToProps, { getAllGolfers })(OverallRankings);

const styles = StyleSheet.create({
  row: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 20
  },
});
