import React, { useEffect } from 'react';
import { StyleSheet, Text, View, SafeAreaView, ScrollView } from 'react-native';
import { connect, useDispatch, useSelector } from 'react-redux';
import { useIsFocused } from '@react-navigation/native';
import { getRabbitCardChoice, getTournamentById } from '../store/redux/tournaments/actions';
import { BackButton, Button, LoadingContainer, Menu, Player, SeparateButton } from '../components/Button';
import { UnderlineIcon } from '../components/icons';
import { CommonColors, CommonStyles } from './style';
import { w, h } from '../utils/scale';

var _ = require('lodash');

const Summary = ({ navigation, route }) => {
  const isFocused = useIsFocused()
  const { card } = route.params

  const dispatch = useDispatch()
  const state = useSelector(state => state.tournaments)


  useEffect(() => {
    isFocused && fetch()
  }, [card])

  const fetch = () => {
    dispatch(getTournamentById(card.t_id))
    dispatch(getRabbitCardChoice(card))
  }

  let holes = _.groupBy(state.choice, 'hole')
  Object.keys(holes).forEach(key => holes[key] = _.groupBy(holes[key], 'group_id'))

  return (
    <View style={CommonStyles.overflowContainer}>
      <BackButton />
      {state.tournaments.loading && <LoadingContainer />}
      <SafeAreaView style={CommonStyles.container}>
        <ScrollView style={CommonStyles.container}>
          <View style={[CommonStyles.padding, { paddingTop: w(100) }]}>
            <Text style={CommonStyles.title}>Rabbit Card Summary</Text>
            <View style={styles.padding20}>
              <UnderlineIcon />
            </View>
            <View style={styles.topSection}>
              <Text style={CommonStyles.subTitle}>{state.selectedTournament?.name || ''}</Text>
              <Text style={CommonStyles.sectionTitle}>{new Date(state.selectedTournament?.start_date).toDateString() || ""} - {new Date(state.selectedTournament?.end_date).toDateString() || ""}</Text>
              <Text style={CommonStyles.sectionTitle}>Round {card?.round || ''}</Text>
              <Text style={[CommonStyles.sectionTitle, { paddingTop: w(40) }]}>RABBIT CARDS BY HOLE</Text>
            </View>
          </View>
          {state.choice.map((hole, index) => {
            const groups = _.groupBy(hole.players, 'group_id')
            return (<View style={styles.section} key={index}>
              <View style={styles.paddingH40}>
                <View style={[styles.row, { alignItems: 'flex-end' }]}>
                  <Text style={CommonStyles.subTitle}>HOLE {hole.id}</Text>
                  <Text style={[CommonStyles.sectionTitle, { paddingLeft: 4 }]}>Par {hole?.par || ""}, {hole?.yardage || ""} Yards</Text>
                </View>
                <View style={styles.row}>
                  {
                    Object.keys(groups).map(groupId =>
                      <View style={styles.rowSection}>
                        {
                          groups[groupId].map((item, index) => {
                            console.log(item, index)
                            return <Player
                              key={index}
                              status={item.won ? 1 : 3}
                              name={item.first_name + " " + item.last_name}
                              onPress={() => navigation.navigate('PlayerProfile', { profile: item })}
                            />
                          }
                          )
                        }
                      </View>
                    )
                  }
                </View>
              </View>
            </View>)
          })}
        </ScrollView>
      </SafeAreaView>
      <SeparateButton prevTitle="RABBIT CARDS" nextTitle="TOURNAMENT" prev={() => navigation.goBack()} next={() => navigation.navigate('TournamentPushNotification', { card })} backgroundColor={CommonColors.Green} />
    </View>
  );
}

const mapStateToProps = (state) => ({
  loading: state.tournaments.loading,
  error: state.tournaments.error,
});

export default connect(mapStateToProps, { getRabbitCardChoice })(Summary);

const styles = StyleSheet.create({
  padding20: {
    paddingVertical: w(20),
  },
  paddingH40: {
    paddingHorizontal: w(40),
    paddingVertical: w(10)
  },
  topSection: {
    paddingTop: w(20)
  },
  section: {
    borderTopWidth: 1,
    borderBottomWidth: 1,
    marginBottom: -1,
    borderColor: CommonColors.Black,
  },
  row: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingVertical: 4,
  },
  rowSection: {
    flex: 1,
    alignItems: 'flex-start',
  }
});
